import React, { useState } from 'react';

const DetailsBanner: React.FC = () => {
    const [isOpen, setIsOpen] = useState(false);

    const openModal = () => {
        setIsOpen(true);
    };

    const closeModal = () => {
        setIsOpen(false);
    };

    return (
        <>
            {
                isOpen && (
                <div className="fixed bottom-0 inset-x-0 p-4 bg-black bg-opacity-50 flex justify-center items-center">
                    <div className="bg-white p-8 rounded-lg shadow-lg">
                    <p className="text-xl font-bold mb-4">Hello World</p>
                    <p className="mb-4">Здесь может быть ваш текст</p>
                    <button
                        className="bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-full"
                        onClick={closeModal}
                    >
                        Закрыть
                    </button>
                    </div>
                </div>
                )
            }
        </>
    );
};

export default DetailsBanner;
