import React, { useState } from "react";
import Draggable from "react-draggable";
import { Resizable, ResizableBox } from "react-resizable";
import { Button as ButtonInterface } from '../scripts/types';

interface ButtonProps extends ButtonInterface {
    index: number,
    onClick: Function,
    isEdit: boolean,
}

const Button: React.FC<ButtonProps> = ({
    index,
    color,
    x,
    y,
    src,
    text,
    isEdit,
    owner,
    price,
    onClick,
}) => {
    const buttonClasses = `p-4 h-16 border text-white font-bold rounded-xl h-full ${ isEdit ? 'edit-banner border-dashed border-2' : 'border-solid' }`;

    return (
        <Draggable
            defaultPosition={{x, y}}
            scale={1}
            handle={isEdit ? '.handle' : '#nothing'}
            bounds={{
                top: 0,
            }}
        >
            <div
                style={{ background: color, }}
                className={buttonClasses}
                onClick={() => onClick(index)}
            >
                { text }
            </div>
        </Draggable>
    );
};

export default Button;
