import React from 'react';
import EmailItem from "../components/EmailItem";
import { greetings, farewells, requests } from "../emailTemplates";


function generateAdvertisementRequest(bannerTopic: string, bannerText: string): string {
    const greeting = greetings[Math.floor(Math.random() * greetings.length)];
    const request = requests[Math.floor(Math.random() * requests.length)];
    const farewell = farewells[Math.floor(Math.random() * farewells.length)];

    // Генерация сообщения
    const message = `
        ${greeting}. ${request}
        Тема баннера: ${bannerTopic}
        Текст баннера: ${bannerText}
        <br>
        ${farewell}
    `;

    return message;
};

const generateEmails = () => {
    const emails = [];
    for (let i = 0; i < 4; i++) {
        emails.push({
            name: "John Doe",
            time: "2 hours ago",
            text: "Hello, how are you?",
            cost: 27,
            isNew: true,
        })
    }
    return emails;
};


function Email() {
    return (
        <div className="tab">
            <div>
                {
                    emails.map((email, index) => (
                        <EmailItem
                            key={index}
                            {...email}
                        />
                    ))
                }
            </div>
        </div>
    );
}

export default Email;
