import React from 'react';
import EmailItem from "../components/EmailItem";


function generateAdvertisementRequest(ownerName: string, bannerTopic: string, bannerText: string): string {
    // Рандомные приветствия, просьбы и прощания

    const requests = [
        "Мы хотели бы предложить вам разместить наш новый баннер на вашем сайте.",
        "Позвольте предложить вам возможность разместить наш баннер на вашем сайте.",
        "Мы предлагаем вам разместить наш баннер на вашем сайте.",
        "Разместите наш новый баннер на вашем сайте, пожалуйста.",
        "Давайте порадуем ваши глаза нашим новым баннером!",
        "Сделайте ваш сайт еще лучше с нашим баннером!",
        "Мы хотим украсить ваш сайт нашим ярким баннером.",
        "Помогите нам сделать ваш сайт креативнее с нашим баннером!",
    ];

    // Случайный выбор приветствия, просьбы и прощания
    const greeting = greetings[Math.floor(Math.random() * greetings.length)];
    const request = requests[Math.floor(Math.random() * requests.length)];
    const farewell = farewells[Math.floor(Math.random() * farewells.length)];

    // Генерация сообщения
    const message = `
        ${greeting} ${ownerName},

        ${request}
        Тема баннера: ${bannerTopic}
        Текст баннера: ${bannerText}

        ${farewell}
    `;

    return message;
    }
  

const emails = [
    {
        name: "John Doe",
        time: "2 hours ago",
        text: "Hello, how are you?",
        cost: 27,
        isNew: true,
    },
    {
        name: "Pes",
        time: "4 hours ago",
        text: "WTF, DUDE?",
        cost: 12,
    },
];

function Email() {
    return (
        <div className="tab">
            <div>
                {
                    emails.map((email, index) => (
                        <EmailItem
                            key={index}
                            {...email}
                        />
                    ))
                }
            </div>
        </div>
    );
}

export default Email;
