import { createSlice } from '@reduxjs/toolkit';
import { PayloadAction } from '@reduxjs/toolkit';
import { Email, Banner, Anal, Rialto, Bot, } from '../scripts/types';

interface User {
   emails: Array<Email>
   banners: Array<Banner>
   anal: Anal,
   rialtos: Array<Rialto>,
   bots: Array<Bot>,
}

const initialState: User = {
   emails: [],
   banners: [],
   anal: {
      balance: 100,
      domen: 'sdsad3fgsa13.xh',
   },
   rialtos: [],
   bots: [],
};

const slice = createSlice({
   name: 'user',
   initialState,

   reducers: {
      setEmail(user, action: PayloadAction<Array<Email>>) {
         user.emails = action.payload;
      },
      setBanner(user, action: PayloadAction<Array<Banner>>) {
         user.banners = action.payload;
      },
      setRialto(user, action: PayloadAction<Array<Rialto>>) {
         user.rialtos = action.payload;
      },
   },
});

export default slice.reducer;
export const { addEmail, addBanner, addRialto, } = slice.actions;
