import React, { useState } from "react";
import Draggable from "react-draggable";
import { Resizable, ResizableBox } from "react-resizable";
import { Link as LinkInterface } from '../scripts/types';

interface LinkProps extends LinkInterface {
    index: number,
    onClick: Function,
    isEdit: boolean,
}

const Banner: React.FC<LinkProps> = ({
    index,
    color,
    x,
    y,
    src,
    text,
    isEdit,
    owner,
    price,
}) => {
    const linkClasses = `p-4 border border-black rounded-xl h-full ${ isEdit ? 'edit-banner border-dashed border-2' : 'border-solid' }`;

    return (
        <Draggable
            scale={1}
            handle={isEdit ? '.handle' : '#nothing'}
            bounds={{
                top: 0,
            }}
        >
            <div className={linkClasses}>
                { text }
            </div>
        </Draggable>
    );
};

export default Banner;
