import { createSlice } from '@reduxjs/toolkit';
import { PayloadAction } from '@reduxjs/toolkit';
import { Email, Banner, Anal, Rialto, Bot, } from '../scripts/types';

interface User {
   emails: Array<Email>
   banners: Array<Banner>
   anal: Anal,
   rialtos: Array<Rialto>,
   bots: Array<Bot>,
}

const initialState: User = {
   emails: [],
   banners: [],
   anal: {},
   rialtos: [],
   bots: [],
};

const slice = createSlice({
   name: 'user',
   initialState,

   reducers: {
      addEmail(user, action: PayloadAction<Email>) {
         user.emails.push(action.payload);
      },
      addBanner(user, action: PayloadAction<Banner>) {
         user.banners.push(action.payload);
      },
      addRialto(user, action: PayloadAction<Rialto>) {
         user.rialtos.push(action.payload);
      },
   },
});

export default slice.reducer;
export const { addEmail, addBanner, addRialto, } = slice.actions;
