import React, { useState } from 'react';
import Anal from './pages/Anal';
import Site from './pages/Site';
import Email from "./pages/Email";
import Rialto from './pages/Rialto';
import { useSelector, useDispatch } from 'react-redux';
import { setActiveTab as setActiveTabAction, } from './redux/user';
import { RootState } from './redux/store';

const Tabs: React.FC = () => {
    const dispatch = useDispatch();
    const emails = useSelector((state: RootState) => state.user.emails);
    const domen = useSelector((state: RootState) => state.user.anal.domain);
    const balance = useSelector((state: RootState) => state.user.anal.balance);
    const activeTab = useSelector((state: RootState) => state.user.activeTab);
    const setActiveTab = (payload: number) => dispatch(setActiveTabAction(payload));

    const tabs = [
        'Панель администратора',
        'Моя аналитика',
        'Моя почта',
        'Фриланс биржа',
    ];
    const urls = [
        domen,
        'https://www.anal.com/',
        'https://www.mail.com/',
        'https://www.easy-money.com/',
    ];

    const getTabUrl = (index: number) => {
        return urls[index];
    };

    const changeTab = (index: number) => {
        setActiveTab(index);
    };

    const getTabContent = (index: number) => {
        switch(index) {
            case 0:
                return <Site/>
            case 1:
                return <Anal/>
            case 2:
                return <Email />;
            case 3:
                return <Rialto/>
        };
    };

    return (
    <div>
        <div className="bg-gray-300 flex space-x-2 px-14 pt-8">
            { tabs.map((tab, index) => (
                index === 2 &&  activeTab !== index && emails.filter(m => m.isNew).length ?
                <button
                    key={index}
                    className={`py-2 px-4 text-gray-600 rounded-t-xl bg-gray-200 w-60`}
                    onClick={() => changeTab(index)}
                >
                    <div className="new-message">
                        У вас новое сообщение!
                    </div>
                </button>
                :
                <button
                    key={index}
                    className={`py-2 px-4 text-gray-600 rounded-t-xl w-60 ${
                        activeTab === index ? 'bg-white' : 'bg-gray-200'
                    }`}
                    onClick={() => changeTab(index)}
                >
                    { tab }
                </button>
            )) }
        </div>

        <div className="flex justify-between items-center bg-gray-200 space-x-2 px-14 py-4">
            <div className="flex justify-between  items-start bg-white rounded w-1/2 px-4 py-2">
                { getTabUrl(activeTab) }
                <svg width="24" height="21" viewBox="0 0 54 51" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M27.9511 2.16312L32.7844 17.0385C33.186 18.2746 34.3379 19.1115 35.6375 19.1115H51.2784C52.2472 19.1115 52.6499 20.3511 51.8662 20.9205L39.2125 30.114C38.161 30.8779 37.721 32.232 38.1227 33.4681L42.956 48.3434C43.2553 49.2648 42.2008 50.0309 41.4171 49.4615L28.7634 40.268C27.7119 39.5041 26.2881 39.5041 25.2366 40.268L12.5829 49.4615C11.7992 50.0309 10.7447 49.2648 11.044 48.3435L15.8773 33.4681C16.279 32.232 15.839 30.8779 14.7875 30.114L2.13377 20.9205C1.35006 20.3511 1.75283 19.1115 2.72156 19.1115H18.3625C19.6621 19.1115 20.814 18.2746 21.2156 17.0385L26.0489 2.16311C26.3483 1.24181 27.6517 1.2418 27.9511 2.16312Z" stroke="#7E7E7E" stroke-width="2"/>
                </svg>

            </div>
            <div className="text-green-500 font-bold text-xl">
                { balance } $
            </div>
        </div>

        <div className="mt-8 px-14">
            { tabs.map((tab, index) => (
                <div
                key={index}
                className={`${activeTab === index ? 'block' : 'hidden'}`}
                >
                    { getTabContent(activeTab) }
                </div>
            )) }
        </div>
    </div>
    );
};

export default Tabs;
