import React, { useState } from 'react';

interface DetailsBannerProps {
    isOpen: boolean;
    onHide: Function;
    bannerId: number;
    title: string;
    owner: string;
    src: string;
    price: number;
}

const DetailsBanner: React.FC<DetailsBannerProps> = ({ isOpen, onHide, bannerId, owner, title, src, price, }) => {
    const closeModal = () => {
        onHide();
    };

    return (
        isOpen ?
        (
            <div className="fixed bottom-0 inset-x-0 p-4 bg-gray-300 flex flex-col items-center shadow-lg">
                <p className="text-xl font-bold mb-4">
                    { title }
                </p>
                <p className="mb-4">
                    Владелец - { owner }
                </p>
                <button
                    className="bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-full"
                    onClick={closeModal}
                >
                    Закрыть
                </button>
            </div>
        )
        :
        null
    );
};

export default DetailsBanner;
